const form=document.querySelector("form");let userInput=document.querySelector("#todotask"),taskData=document.querySelector(".todo-content");function createTodo(e){let t=e.target.todotask.value,n=e.target.description.value,a=new Date;const o=document.createElement("div");o.className="wraptask",o.innerHTML=`\n  <i class="fa-solid fa-trash-can btn"></i>\n  <div class="taskitems">\n    <h3 id="tasktitle">${t}</h3>\n    <p id="taskdesc">${n}</p>\n    </div>\n    <div id="actions">\n    <p id="date">${a.toDateString()}</p>\n    <p id="time">${a.toLocaleTimeString()}</p>  \n    </div>    \n    `,document.querySelector(".todo-content").appendChild(o),saveData(),document.querySelectorAll(".btn").forEach((e=>{e.addEventListener("click",handleDelet)}))}function handleDelet(e){e.target.parentNode.remove(),saveData()}function clock(){let e=new Date,t=e.toLocaleDateString("en-US",{weekday:"long"}),n=e.getHours(),a=e.getMinutes(),o=e.getSeconds();n=checkTime(n),a=checkTime(a),o=checkTime(o),document.getElementById("clock").innerHTML=n+":"+a+":"+o,setTimeout(clock,1e3),document.querySelector("#day").textContent=t}function checkTime(e){return e<10&&(e="0"+e),e}function saveData(){localStorage.setItem("todos",taskData.innerHTML),console.log("saved data")}function showTasks(){taskData.innerHTML=localStorage.getItem("todos"),console.log("got the data")}form.addEventListener("submit",(e=>{e.preventDefault(),createTodo(e),form.reset()})),clock(),showTasks(),taskData.addEventListener("click",(e=>{e.target.classList.contains("btn")&&handleDelet(e)}));